import React, { useEffect, useState } from 'react';
import styled from '@emotion/styled';
import { Doughnut } from 'react-chartjs-2';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLocationArrow } from '@fortawesome/free-solid-svg-icons';

const Wind = styled("div")` 
display: flex;
position: relative;
height: 100%;
flex-direction: row;
flex-wrap: wrap;
justify-content: center;
align-content: center;
background: #fff;
box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16);

.rotate {
  transform: rotate(-45deg);
}
.locationArrow {
  position: absolute;
  margin-top: 140px;
}

`

const data = {
  datasets: [{
    label: "Gauge",
    data: [0, 360],
    backgroundColor: [
      "rgb(255, 99, 132)",
      "rgb(54, 162, 235)",
      "rgb(255, 205, 86)"
    ]
  }]
}

const options = {

  circumference: Math.PI * 2,
  rotation: Math.PI,
  cutoutPercentage: 70, // precent
  plugins: {
    datalabels: {
      backgroundColor: 'rgba(0, 0, 0, 0.7)',
      borderColor: '#ffffff',
      color: function (context) {
        return context.dataset.backgroundColor;
      },
      font: function (context) {
        var w = context.chart.width;
        return {
          size: w < 512 ? 18 : 20
        }
      },
      align: 'start',
      anchor: 'start',
      offset: 10,
      borderRadius: 4,
      borderWidth: 1,
      formatter: function (value, context) {
        var i = context.dataIndex;
        var len = context.dataset.data.length - 1;
        if (i === len) {
          return null;
        }
        return value + ' mph';
      }
    }
  },
  legend: {
    display: false
  },
  tooltips: {
    enabled: false
  }
}

export const WindChart = ({ windDirection }) => {
  const [rotation, setRotation] = useState({
    rotate: false,
  });

  return (
    <Wind>
      <Doughnut data={data} option={options} />
      <FontAwesomeIcon className='locationArrow' transform size='2x' color='red' icon={faLocationArrow} />
      <button onClick={() => { setRotation({ rotate: true }) }}>click</button>
    </Wind >
  )
}
