import { initialState } from '../data/initialState';
const { area } = initialState;

const areaReducer = (state = area, action) => {
  const { name } = action.payload;
  switch (action.type) {
    case '':
      return {
        area: { ...state, name }
      }
    default:
      return state;
  }

}

export default areaReducer;
