import React from 'react';
import styled from "@emotion/styled";
import size from "../styles/size";
import colors from '../styles/colors';

const Container = styled("main")`
    flex: 1;
    grid-area: main;
    padding: ${size.spacing};
    overflow-y: auto;
    overflow-x: hidden;
    -webkit-overflow-scrolling: touch;
    background-color: ${colors.main};
`
const Dasboard = styled("div")`
  --column-count: 2;
  display: grid;
  grid-template-columns: repeat(var(--column-count), 1fr);
  grid-gap: ${size.spacing};
`
const Item = styled("div")`
   flex: 1 1 50%;
    grid-column-end: span 2;
    padding: calc(var(${size.spacing}) / 2);
`

export const Main = () => {
  return (
    <Container>
      <h2>Dashboard</h2>
      <Dasboard>
        <Item>
          <div className="card">
            <strong>41</strong> Foobars
        </div>
        </Item>
        <div className="dashboard__item">
          <div className="card">
            <strong>81.712</strong> Doohickeys
        </div>
        </div>
        <div className="dashboard__item dashboard__item--full">
          <div className="card">
            <img src="https://imgs.xkcd.com/comics/decline.png" alt="" />
          </div>
        </div>
        <div className="dashboard__item dashboard__item--col">
          <div className="card">A</div>
        </div>
        <div className="dashboard__item dashboard__item--col">
          <div className="card">B</div>
        </div>
        <div className="dashboard__item dashboard__item--col">
          <div className="card">C</div>
        </div>
        <div className="dashboard__item dashboard__item--col">
          <div className="card">D</div>
        </div>
      </Dasboard>
      <p>read more about this in <a href="https://mxb.at/blog/css-grid-admin-dashboard/">this blogpost</a>.</p>
    </Container >
  )
}
