import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import coqSummitData from '../data/coqSummitData.json';

export const Chart = () => {
  const [snowData, setSnowData] = useState({
    labels: [],
    data: [],
  })
  useEffect(() => {
    const getSnowPackData = (arr) => {
      const dataArray = [];
      const dateArray = [];
      for (let i = 0; i < arr.length; i++) {
        if (arr[i].date.charAt(11) === "2" && arr[i].date.charAt(12) === "3") {
          dateArray.push(arr[i].date)
          dataArray.push(arr[i].snowpack);
        }
      }
      return { dataArray, dateArray };
    };
    const historicSnowData = getSnowPackData(coqSummitData);
    setSnowData({
      date: historicSnowData.dateArray,
      data: historicSnowData.dataArray
    })
  }, [])
  console.log(snowData)
  return (
    <div className="chart">
      <Line
        data={snowData}
        options={{
          title: {
            display: true,
            text: "Snow Depth for the Coquihalla Area"
          },
          legend: {
            display: true,
            position: 'right'
          }
        }} />
    </div>
  );
};
