import React, { useState, useEffect } from 'react';

export const DropDown = ({ data }) => {
  const [stationsData, setStationsData] = useState(data)
  const [weatherStation, setWeatherStation] = useState();
  useEffect(() => {
    setStationsData(data);
  }, [data]);

  useEffect(() => {
    const getStationSnowDepth = (stationData, weatherStation) => {
      stationsData.find(station => {
        if (station.station.name === weatherStation) {
          return station;
        } else {
          return [];
        }
      }
      )
    }
    console.log(getStationSnowDepth())
  }, [stationsData, weatherStation]);

  return (
    <div>
      <form>
        <label htmlFor="station">
          Weather Station
        <select
            id="station"
            value={weatherStation}
            onChange={e => setWeatherStation(e.target.value)}
            onBlur={e => setWeatherStation(e.target.value)}>
            <option>Select Station...</option>
            {stationsData.map(station => (
              <option key={station.station.id} value={station.station.name}
              >
                {station.station.name}
              </option>
            ))}
          </select>
        </label>
        <input type="submit" value="Submit" />
      </form>
      <h2>{weatherStation}</h2>
    </div>
  )
}
