import React, { useEffect, useState } from 'react';
import './App.css';
import { DropDown } from './components/DropDown';
import { StationDetails } from './components/StationDetails'

function App() {
  const [weather, setWeather] = useState({
    weatherStations: []
  });

  useEffect(() => {
    const getWeather = async () => {
      const url = `https://www.drivebc.ca/api/weather/observations?format=json`;
      const response = await fetch(url);
      const data = await response.json();
      const stationNameStrings = data.map((station) => station.station.name)
      setWeather({
        weatherStations: stationNameStrings
      })
    }
    getWeather();
  }, [])
  return (
    <div>
      <DropDown data={weather.weatherStations} />
      <StationDetails />
    </div>

  );
}

export default App;
