import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';

export const Chart = ({ data }) => {
  const [totalSeasonSnowFall, setTotalSeasonSnowFall] = useState(data);
  useEffect(() => {
    const getSnowPackData = (data) => {
      const newArray = [];
      for (let i = 0; i < data.length; i++) {
        if (data[i].date.charAt(11) === "2" && data[i].date.charAt(12) === "3") {
          newArray.push(data[i].snowpack);
        }
      }
      return newArray;
    };
    const historicSnowData = getSnowPackData(totalSeasonSnowFall);
    setData(historicSnowData);
  }, [totalSeasonSnowFall])
  return (
    <div className="chart">
      <Line
        data={data}
        options={{
          title: {
            display: true,
            text: "Snow Depth for the Coquihalla Area"
          },
          legend: {
            display: true,
            position: 'right'
          }
        }} />
    </div>
  );
};
