import React, { useState, useEffect } from 'react';

export const DropDown = ({ data }) => {
  const [weatherData, setWeatherData] = useState({
    stationsList: []
  })
  const [weatherStation, setWeatherStation] = useState({
    selectedStation: ""
  })
  useEffect(() => {
    setWeatherData({ stationsList: data })
  }, [data]);

  // const handleChange = (event) => {
  //   this.setState({ value: event.target.value });
  // }

  // handleSubmit = (event) => {
  //   alert('Your favorite flavor is: ' + this.state.value);
  //   event.preventDefault();
  // }
  return (
    <form>
      <label htmlFor="station">
        Weather Station
        <select id="station" value={weatherStation} onChange={e => setWeatherStation} onBlur={e => setWeatherData}>
          {weatherData.stationsList.map(station => (
            <option key={station.station.id} value={station.station.name}>
              {station.station.name}
            </option>
          ))}
        </select>
      </label>
      <input type="submit" value="Submit" />
    </form>
  )
}
