import React, { useEffect } from 'react';
import { Bar, Line, Pie } from 'react-chartjs-2';
import coqSummitData from '../data/coqSummitData.json';



export const Chart = () => {
  useEffect(() => {
    const getSnowPackData = (data) => {
      const newArray = [];
      for (let i = 0; i < data.length; i++) {
        if (data[i].date.charAt(11) === "2" && data[i].date.charAt(12) === "3") {
          newArray.push(data[i].snowpack);
        }
      }
      return newArray;
    };
    const data = getSnowPackData(coqSummitData);
  }, )
  return (
    <div className="chart">
      <Line
        data={data}
        options={{
          title: {
            display: true,
            text: "Snow Depth for the Coquihalla Area"
          },
          legend: {
            display: true,
            position: 'right'
          }
        }} />
    </div>
  );
};
